    <script type="text/javascript">
    	setInterval(sale_check, 300000);
    	setInterval(session_check, 600000);
		function sale_check(){

			var toto = $('#total_sale').val();

			$.ajax({

				url: 'https://ryants.com/demo/store/vendor/sales/total/'+toto,

				success: function(data) {

					var new_sale = data-toto;

					$('#total_sale').val(data);

					if(new_sale > 0){

		                $.activeitNoty({

		                    type: 'success',

		                    icon : 'fa fa-check',

		                    message : new_sale+' More Sale(s)!',

		                    container : 'floating',

		                    timer : 8000

		                });

						sound('sale_audio');

		            }

				},

				error: function(e) {

					console.log(e)

				}

			});

		}
		function session_check(){

			$.ajax({

				url: 'https://ryants.com/demo/store/vendor/is_logged/',

				success: function(data) {

					if(data == 'yah!good'){}

					else if (data == 'nope!bad') {

						location.replace('https://ryants.com/demo/store/vendor');

					}

				},

				error: function(e) {

					console.log(e)

				}

			});

		}
		$(document).ready(function(){
			$('#content-container').css('min-height',$('#mainnav-menu').height()+'px');
		});
    </script>
